import React from "react";
import '../style.css';
import { Modal, Button } from "react-bootstrap";
import SignUp from "./SignUp";
import login from "./login";
class Header extends React.Component{
  constructor(...args) {
    super(...args);

    this.state =
    {
      modalShow: false,
      modalShow2: false
    };
  }

  render(){
    let modalClose = () => this.setState({ modalShow: false });
    let modalClose2 = () => this.setState({ modalShow2: false });
    return (
      <div>
        <header id = "bg2">
          <button
            className="logo_button"
            type="button">
              <img src="http://razzlers.me:8080/Razzlers_files/Razzlers-Logo.png" height = "80" padding = "10"/>
          </button>
          <button
            className="redirects"
            type="button">
            NetFlix
          </button>
          <button
            className="redirects"
            type="button">
            Hulu
          </button>
          <button
            className="redirects"
            type="button">
            Youtube
          </button>
          <button
            className="redirects"
            type="button">
            Members
          </button>
          <button
            className="button"
            type="button">
              Log In
          </button>
          <button
            onClick={() => this.setState({ modalShow: true })}
            className="button"
            type="button">
              Sign Up
          </button>
        </header>
        <SignUp
          show={this.state.modalShow}
          onHide={modalClose}/>
        <login
          show={this.state.modalShow2}
          onHide={modalClose2}/>
      </div>
    )
  }
}
export default Header
