import React from "react";
import '../style.css';

class PlayVideo extends React.Component {
  constructor(props)
  {
    super(props)
    this.state = {
      isMovie: false,
      id: 0
    }
    checkParams().then(json => {
      this.setState(json);
    });
  }
  render() {
    const {isMovie, showID} = this.state;
    console.log(this.state);
    return (
      <div>
      <h2><font color ="white" size = "50">Movie Title</font></h2>

      <video width="720" height="480" controls>
        <source src="https://fs1.rovault.org/d2d63747331dc977?download_token=dadf39e56b642fd8fbd715c32f79f7a3d771b46ada49ccc48d9acfd137caf136" type="video/mp4"/>
      </video>


      </div>
    );
  }
}

function checkParams()
{
  return new Promise(function(resolve, reject)
  {
    console.log("params");
    var url = window.location.href;
    url = new URL(url);
    var isMovie = url.searchParams.get("isMovie");
    var id = url.searchParams.get("id");
    resolve({isMovie, id});
  });
}

