import React from 'react';
const utilFunc = require('../Helpers/UtilityFunctions');

class MessageCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      sender: props.sender,
      content: props.content,
      time: props.time
    };
    utilFunc.getUsernameFromID(props.sender).then(result=> {this.setState({sender:result.username})})
  }

  render()
  {
    const {content, sender, time} = this.props;
    return (
      <div className="messageBubble">
        <font color = "white">{sender}</font>
        <font color = "white">{content}</font>
        <font color = "white">{time}</font>
      </div>);
  }
}

export default MessageCard;
