import React from "react";
import '../style.css';

class PlayVideo extends React.Component {
  constructor(props)
  {
    super(props)
    this.state = {
      isMovie: false,
      id: 0
    }
    checkParams().then(json => {
      this.setState(json);
    });
  }
  render() {
    const {isMovie, id} = this.state;
    var name = "";
    var description = "";
    var rating = 0;
    var actors = "";
    var release_year = 0;
    if(id !== 0)
    {
      // set constants here and use them for page info
      var set = getVideoInfo(isMovie, id);
      if(set.hasOwnProperty("result"))
      {
        // result from server didnt pull the correct file and it doesnt exist!
        console.log("file doesnt exist!");
      } else {
        // parse good response into constants for current state
        // {title, desc, rate, act, year};
        name = set.title;
        description = set.desc;
        rating = set.rate;
        actors = set.act;
        release_year = set.year;
      }
    }
    var loc = "";
    if(isMovie === "true")
    {
      loc = "//razzlers.me/assets/videos/movies/" + id + ".mp4";
    } else {
      loc = "//razzlers.me/assets/videos/episodes/" + id + ".mp4";
    }
    return (
      <div>
        <h2 className="centerText"><font  color ="white" size = "50">Movie Title</font></h2>

        <video className="center" width="720" height="480"   controls>
          <source src={loc} type="video/mp4"/>
        </video>
        <p className="centerText"><font  color ="white" size = "20px">Rating; Movie Description cody like dick in and around his ass</font></p>
        <p className="quarterLeft"><button className= "button">Previous Episode</button></p>
      </div>
    );
  }
}

function checkParams()
{
  return new Promise(function(resolve, reject)
  {
    var url = window.location.href;
    url = new URL(url);
    var isMovie = url.searchParams.get("isMovie");
    var id = url.searchParams.get("id");
    resolve({isMovie, id});
  });
}
function getVideoInfo(isMovie, id)
{
  return new Promise(function(resolve, reject)
  {
    var data = '{"isMovie": "' + isMovie + '", "id": "' + id + '"}';
    data = JSON.parse(data);
    var transport = {
      headers: {
        'Content-Type': "application/json"
      },
      method: "PUT",
      body: JSON.stringify(data)
    };
    const url = "http://localhost:3001/api/getData/getVideoInfo";
    fetch(url, transport).then(result => result.json()).then(json => {
      console.log(json);
      resolve(json);
      if(1 === 1) {} else { reject(); }
    }).catch(err => {
      throw new Error(err);
    });
  });
}

export default PlayVideo
