  constructor(props)
  {
    super(props)
    this.state = {
      movieList: 0,
      showList: 0,
      search: ""
    };
    getSearch().then(result => {this.setState({ search: result});});
    console.log(this.state.search);
  }
  render() {
    const {movieList, showList, search} = this.state;
