import React from "react";
import '../style.css';
class SignUp extends React.Component
{
  render()
  {
    return (
      <div className = "bg2" align = "left">
        <font size= "26" >Sign Up</font>

        <input
          type="text"
          placeholder="Email"
          className="input"/>

        <input
          type="text"
          placeholder="First Name"
          className="input"/>

        <input
          type="text"
          placeholder="Last Name"
          className="input"/>

        <input
          type="text"
          placeholder="Display Name"
          className="input"/>

        <input
          type="text"
          placeholder="Username"
          className="input"/>
        <input
          type="password"
          placeholder="Password"
          className="input"/>

        <div className="tos">
          <input type="checkbox" name="terms"></input>
            <font> I Agree Terms & Coditions</font>
            <br></br>
        </div>

        <button  className="button2">
          SignUp
        </button>

        <button  className="button2">
          Login
        </button>
      </div>
    );
  }
}

function loginVerification(inUsername, inPassword)
{
  var data = '{"username": "' + inUsername + '", "password": "' + inPassword + '"}';
  data = JSON.parse(data);
  var transport = {
    headers: {
      'Content-Type': "application/json"
    },
    method: "POST",
    body: JSON.stringify(data)
  };
  const url = "http://localhost:3001/api/register";
  fetch(url, transport).then(response => response.json()).then(json => {
    var serverResponse = json.result;
    console.log(serverResponse);
    if(serverResponse === "true")
    {
      localStorage.setItem("username", inUsername);
    } else {
      // reject the user, tell invalid password try again
      localStorage.setItem("username", null);
      return;
    }
  });
}

export default SignUp
