import React from "react";
import '../style.css';

class PlayVideo extends React.Component {
  constructor(props)
  {
    super(props)
    this.state = {
      isMovie: false,
      id: 0
    }
    checkParams().then(json => {
      this.setState(json);
    });
  }
  render() {
    const {isMovie, id} = this.state;
    console.log(this.state);
    return (
      <div >
        <h2 className="centerText"><font  color ="white" size = "50">Movie Title</font></h2>

        <video className="center" width="720" height="480"   controls>
          <source src="//Razzlers.me/10.mp4" type="video/mp4"/>
        </video>
        <p className="centerText"><font  color ="white" size = "20px">Rating; Movie Description cody like dick in and around his ass</font></p>
        <p margin-left="25%"><button className= "button">Previous Episode</button></p>
      </div>
    );
  }
}

function checkParams()
{
  return new Promise(function(resolve, reject)
  {
    var url = window.location.href;
    url = new URL(url);
    var isMovie = url.searchParams.get("isMovie");
    var id = url.searchParams.get("id");
    resolve({isMovie, id});
  });
}

