function forceUpdateMessages() {
  var sender = window.localStorage.getItem("Razzlers_Username");
  var recip = document.getElementById("messageTitle").innerHTML;

  if (sender !== recip) {
    utilFunc.getUsersMessages(sender, recip).then(function (messages) {
      thisref.setState({
        defaultMessage: messages.messages
      });
    });

    if (thisref.state.unreadMessages.length !== 0 && sender !== null && recip !== null && document.getElementById("messages").hidden === false) {
      utilFunc.readUserMessages(recip, sender);
    }
  }
}

function sendMessage(recipient, messageText) {
  return new Promise(function (resolve, reject) {
    var data = '{"sender": "' + window.localStorage.getItem("Razzlers_Username") + '", "recipient": "' + recipient + '", "message": "' + messageText + '"}';
    data = JSON.parse(data);
    var transport = {
      headers: {
        'Content-Type': "application/json"
      },
      method: "PUT",
      body: JSON.stringify(data)
    };
    var url = "http://razzlers.me:3001/api/sendMessage";
    fetch(url, transport).then(function (result) {
      return result.json();
    }).then(function (json) {
      resolve(json);
    }).catch(function (err) {
      throw new Error(err);
    });
  });
}

function getUserList() {
  return new Promise(function (resolve, reject) {
    var transport = {
      headers: {
        'Content-Type': "application/json"
      },
      method: "GET"
    };
    var url = "http://razzlers.me:3001/api/getData/getUserList";
    fetch(url, transport).then(function (result) {
      return result.json();
    }).then(function (json) {
      resolve(json);
    }).catch(function (err) {
      throw new Error(err);
    });
  });
}

function searchUsers(search) {
  return new Promise(function (resolve, reject) {
    getUserList().then(function (users) {
      var s = search.toLowerCase();

      for (var i = users.users.length - 1; i >= 0; i--) {
        if (!users.users[i].toLowerCase().includes(s)) {
          users.users.splice(i, 1);
          users.IDs.splice(i, 1);
        }
      }

      resolve({
        users: users
      });
    });
  });
}

export default Footer;

