import React from "react";
import '../style.css';
import envelope from '../images/envelope.png';
import friends from '../images/friends.png';

class Footer extends React.Component
{
  render()
  {
    var username = window.localStorage.getItem("Razzlers_Username");
    if(username !== null)
    {
      getUsersFriends(username);
      return (
        <div>
          <div className="footerMenu">
            <form role="search">
              <div className="form-group">
                <input type="text" className="form-control" placeholder="Search Users" id = "search"/>
              </div>
              <button type="submit" className="button3" onClick={() =>
                  {
                    window.location.href='/search?search=' + document.getElementById("search").value;
                    window.event.preventDefault();
                  }}>Search</button>
            </form>
            <h1><font color= "white">Friends</font></h1>
            <ul>
              <li>hello</li>
              <li>I am</li>
              <li>connor</li>
            </ul>

          </div>
          <div className="footer">
            <button className = "iconButton">
              <img src = {envelope}/>
            </button>
            <button className = "iconButton">
              <img src = {friends}/>
            </button>
          </div>
        </div>
      );
    }
    return(
      <div>
      </div>
    )
  }
}
function getUsersFriends(user)
{
  return new Promise(function(resolve, reject)
  {
    var data = '{"username": "' + user + '"}';
    data = JSON.parse(data);
    var transport = {
      headers: {
        'Content-Type': "application/json"
      },
      method: "PUT",
      body: JSON.stringify(data)
    };
    const url = "http://localhost:3001/api/getData/getUsersFriends";
    fetch(url, transport).then(result => result.json()).then(json => {
      console.log(json);
      resolve(json);
    }).catch(err => {
      throw new Error(err);
    });
  });
}

export default Footer
